import React, { Component } from 'react';

class Counter extends Component {


    // constructor(){
    //     super()
    //     this.increaseCount = this.increaseCount.bind(this)
    // }

    styles = {
        fontSize : 20,
        fontWeight: "bold"
    }


    render() { 
        console.log("Props",this.props.counter)

        return (
            <React.Fragment>
                <h4>{this.props.children}</h4>
                <span style={this.styles} className='badge badge-primary m-2'>{this.formatCount()}</span>

                <button onClick={ () => this.props.onIncreament()}  className='btn btn-primary m-5'>Increment</button>
                <button onClick={this.props.onRemove} className='btn btn-danger'>Delete</button>

            </React.Fragment>
        );
    }

    formatCount = ()=>{
        const {count}  = this.props.counter.value;
        console.log("Thsi is count: ",count)
        return count === 0 ? <h1>Zero</h1> : count;
    }
}

export default Counter;
