import React, { Component } from 'react';

class Counter extends Component {

    styles = {
        fontSize : 20,
        fontWeight: "bold"
    }

    formatCount (){
        const {count}  = this.props.value;
        console.log("Thsi is count: ",this.props.value)
        return count === 0 ? <h1>Zero</h1> : count;
    }
    render() { 
        console.log("Props",this.props.counter)

        return (
            <React.Fragment>
                <h4>{this.props.children}</h4>
                <span style={this.styles} className='badge badge-primary m-2'>{this.formatCount()}</span>

                <button onClick={ () => this.props.onIncreament(this.props.counter)}  className='btn btn-primary m-5'>Increment</button>
                <button onClick={this.props.onRemove} className='btn btn-danger'>Delete</button>

            </React.Fragment>
        );
    }


}

export default Counter;
